import React, {Component} from 'react';
import PropTypes from 'prop-types';
import './BurgerIngredient.css';

class BurgerIngredient extends Component {
render() {

    let ingredient = null;
// to analyse type of the ingredient. Type is the property expected to receive
    switch (this.props.type){
        case ('bread-bottom'):
            ingredients =<div className='BreadBottom'></div>;
            break;
        case ( 'bread-top'):
           ingredients = (
               <div className = 'BreadTop'>
                   <div className ='Seeds1'></div>
                   <div className ='Seeds2'></div>
               </div>
           );
           break;
        case ('meat'):
            ingredients= <div className = 'Meat'> </div>;
            break;

        case ('cheese'):
                ingredients= <div className = 'Cheese'></div>;
                break;

        case ('bacon'):
                    ingredients= <div className = 'Bacon'></div>;
                    break;
        case ('salad'):
                        ingredients= <div className = 'Salad'></div>;
                        break;
default: 
ingredient = null;
           }

return ingredient;

}
}


BurgerIngredient.propTypes = {
    //to make sure the ingredients passed is string
    type: PropTypes.string.isRequired
};

export default BurgerIngredient;
