import React, {Component} from 'react';
import Button from '../../../components/UI/Button/Button';
import classes from './ContactData.module.css';
import axios from '../../../axios-orders';
import Spinner from '../../../components/UI/Spinner/Spinner';
import Input from '../../../components/UI/Input/Input';

class ContactData extends Component {
    // to create form dynamically 

    state = {
        orderForm: {

                name: {
                    elementType: 'input',// HTML tag,input 
                    elementConfig: {
                        type: 'text',
                        placeholder: 'Your Name'
                    },
                    value: ''
                },



                street: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'text',
                        placeholder: 'Street'
                    },
                    value: ''
                },
                zipCode: {
                    elementType: 'input',
                    elementConfig: {
                        type: 'text',
                        placeholder: 'Zip Code'
                    },
                    value: ''
                },
                country: {
                    elementType: 'input', 
                    elementConfig: {
                        type: 'text',
                        placeholder: 'Your Country'
                    },
                    value: ''
                },
                email: {
                    elementType: 'input',// HTML tag,input 
                    elementConfig: {
                        type: 'email',
                        placeholder: 'Your Email'
                    },
                    value: ''
                },
                delivery: {
                    elementType: 'select',
                    elementConfig: {
                        options: [ 
                            { value: 'fastest', displayValue: 'Fastest'},
                            { value: 'cheapeast', displayValue: 'Cheapest'}
                        ]
                    },
                    value: ''
                }

        },
        loading: false
    }
    orderHandler =(event) => {
            event.preventDefault();

            console.log(this.props.ingredients);
            // loading: true because request is being sent
        this.setState({loading: true});

        const order = {
            ingredients: this.props.ingredients,
            price: this.props.price,

        }

    // to send/ store data
    // for firebase put .json format, orders is the node that gets created in firebase 
    axios.post('/orders.json', order)
    .then(response => {
        //To stop showing the spinner, to stop loading, purchasing: false => to not display modal 
       this.setState({ loading: false});
       this.props.history.push('/');

   })
   .catch(error => {
    //To stop showing the spinner, to stop loading
   this.setState({ loading: false});
   });
}
render(){
    // to convert orderForm into an array so we can loop through, key is name, street,country ets and value is the whole value in them  
    const formElementsArray = [];
    for ( let key in this.state.orderForm) {
        formElementsArray.push ({
            id: key,
            confif: this.state.orderForm[key]
        });

    }
    let form = (
        <form>
            {/* should pass 3 items */}

                    {formElementsArray.map(formElement => (
                        <Input 
                            key ={formElement.id}
                            elementType = {formElement.config.elementType}
                            elementConfig = {formElement.config.elementConfig}
                            value = { formElement.config.value}/>
                    ))}

                    <Button btnType= "Success" clicked = { this.orderHandler} >Order</Button>



                </form>
    );
    if (this.state.loading) {
        form = <Spinner />;
    }
    return(
            <div className = { classes.ContactData}>
                <h4> Enter your Contact Details</h4>
                {form}


            </div>
        )
    }
}

export default ContactData;
