import Order from '../../components/Order/Order';
import axios from '../../axios-orders';

class Orders extends Component {
    state = {
        orders: [],
        loading: true
    }
    // to fetch orders from firebase

    componentDidMount(){
        axios.get('/orders.json')
        .then(res => {
            this.setState({loading: false});

        })
        .catch(err => {
            this.setState({loading: false});
        });
    }
    render () {
        return (
            <div>
                <Order />
                <Order />
                <Order />

            </div>
        );
    }
}

export default Orders;
